package ch03_operator;

public class OpEx9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 100;
		System.out.println(num);
		num +=10;
		System.out.println(num);
		num/=10;
		System.out.println(num);
		num *= 2+3;
		System.out.println(num);
		
		System.out.println(" ");
		byte b = 1;
		b*=2;
		b = (byte) (b*2);
		System.out.println(b);
		b *=2;
		System.out.println(b);
		b *=2;
		System.out.println(b);
	}

}
